import React from 'react';
import PropTypes from 'prop-types';
import { withStyles } from '@material-ui/core/styles';
// Constants
import { BASE_IMG_URL } from '../constants/constants';
// Material Components
import Grid from '@material-ui/core/Grid';
import Typography from '@material-ui/core/Typography';
import Chip from '@material-ui/core/Chip';

import Card from '@material-ui/core/Card';
import CardContent from '@material-ui/core/CardContent';
import CardMedia from '@material-ui/core/CardMedia';
import CardActions from '@material-ui/core/CardActions';

import List from '@material-ui/core/List';

import ExpansionPanel from '@material-ui/core/ExpansionPanel';
import ExpansionPanelSummary from '@material-ui/core/ExpansionPanelSummary';
import ExpansionPanelDetails from '@material-ui/core/ExpansionPanelDetails';

import ExpandMoreIcon from '@material-ui/icons/ExpandMore';
// Components
import LocationDetails from './LocationDetails';

const styles = theme => ({
  root: {
    flexGrow: 1,
  },
  card: {
    maxWidth: 345,
    minHeight: 415,
  },
  cardContent: {
    padding: 0,
  },
  media: {
    // ⚠️ object-fit is not supported by IE 11.
    objectFit: 'cover',
    margin: `auto`,

  },
  griditem: {
    padding: theme.spacing.unit * 2,
  },
  listAnno: {
    width: '100%',
    '& div:last-of-type': {
      border: 0,
    }
  },
  heading: {
    fontSize: theme.typography.pxToRem(20),
    fontWeight: theme.typography.fontWeightRegular,
  },
  expanPanDet: {
    flexWrap: 'wrap',
  },
});

const CounterChip = withStyles({
  root:  {
    marginLeft: '10px',
    height: '22px',
  },
  label: {
    paddingLeft: '8px',
    paddingRight: '8px',
  }
})(Chip);

function DataExpansionPanel(props) {
  const { classes, data } = props;

  return (
    <div>
      <Grid container justify="center" spacing={24}>
        {data.map(region => (
          <Grid item xs={3} sm={7} className={classes.griditem}>
            <Card 
              key={region.id}
              count={region.count}
              location={region.location}
              location_img={region.location_img}
              locations={region.locations}
              className={classes.card}
            >
              <Typography variant="headline" component="h3">
                {region.location}
                <CounterChip label={region.count}></CounterChip>
              </Typography>
              <CardMedia
                component="img"
                className={classes.media}
                height="auto"
                image={region.location_img && ( BASE_IMG_URL+region.location_img )}
                title={region.location}
                alt-title={region.location}
              />
              <CardContent className={classes.cardContent}>
                <List className={classes.listAnno} disablePadding={true} component="div">
                  {region.locations.map(data => (
                      <LocationDetails 
                        data={data}
                      />
                  ))}
                </List>
              </CardContent>
              <CardActions>
              </CardActions>
            </Card>
          </Grid>
        ))}
      </Grid>
    </div>
  );

  /*return (
    <div>
      {data.map(region => (
        <ExpansionPanel
          key={region.id}
          count={region.count}
          location={region.location}
          location_img={region.location_img}
          locations={region.locations}
          className={classes.root}
        >
          <ExpansionPanelSummary expandIcon={<ExpandMoreIcon />}>
            <Typography className={classes.heading}>
              {region.location} 
              <CounterChip label={region.count}></CounterChip>
            </Typography>
          </ExpansionPanelSummary>
          <ExpansionPanelDetails className={classes.expanPanDet}>
            {region.location_img && (<img src={BASE_IMG_URL+region.location_img} alt={region.location} width='100%' />)}
            {region.locations.map(data => (
              <LocationDetails 
                data={data}
              />
            ))}
          </ExpansionPanelDetails>
        </ExpansionPanel>
      ))}
    </div>
  );*/
}

DataExpansionPanel.propTypes = {
  classes: PropTypes.object.isRequired,
};

export default withStyles(styles)(DataExpansionPanel);
