// Theme
import { createMuiTheme } from '@material-ui/core/styles';

const DarkTheme = createMuiTheme({
  palette: {
    type: 'dark',
  },
});

const LightTheme = createMuiTheme({
  palette: {
    type: 'light',
  },
});

const SetTheme = mode => (mode === 'light' ? LightTheme : DarkTheme);

export default SetTheme;
