import React from 'react';
import PropTypes from 'prop-types';
import { withStyles } from '@material-ui/core/styles';
import { NavLink } from 'react-router-dom';
// Material Components
import BottomNavigation from '@material-ui/core/BottomNavigation';
import BottomNavigationAction from '@material-ui/core/BottomNavigationAction';
import RestoreIcon from '@material-ui/icons/Restore';
import FavoriteIcon from '@material-ui/icons/Favorite';
import LocationOnIcon from '@material-ui/icons/LocationOn';

const styles = {
  root: {
    position: 'fixed',
    left: 0,
    right: 0,
    bottom: 0,
    borderTop: '1px solid #303030',
    height: 'auto',
    '& a': {
      textDecoration: 'none',
    },
    '& .MuiBottomNavigationAction-label': {
      background: 'transparent',
      padding: '0 10px',
      transition: '.3s',
    }
  },
  activeNavlink: {
    '& .MuiBottomNavigationAction-label': {
      background: '#905a08',
    }
  },
  active: {}
};

class SimpleBottomNavigation extends React.Component {
  state = {
    route: '/',
  };

  updateRoute = (event, route) => {
    let newRoute = event.currentTarget.attributes.route.nodeValue;
    this.setState({ route: newRoute });
  };

  render() {
    const { classes } = this.props;
    const { route } = this.state;
    const prayerBeadsIcon = <img src='/assets/images/prayer-bead.png' alt="logo" width="50" />,
          gourdSeedsIcon = <img src='/assets/images/gourd-seed.png' alt="logo" width="50" />,
          memoriesIcon = <img src='/assets/images/memories.png' alt="logo" width="50" />,
          prostheticsIcon = <img src='/assets/images/prosthetic.png' alt="logo" width="50" />,
          scaleIcon = <img src='/assets/images/treasure-carp-scale.png' alt="logo" width="50" />;

    return (
      <BottomNavigation
        route={route}
        showLabels
        className={classes.root}
      >
        <NavLink exact activeClassName={classes.activeNavlink} to="/" route="/prayer-beads" onClick={this.updateRoute}>
          <BottomNavigationAction showLabel={true} label="Prayer Beads" aria-label="Prayer Beads" icon={prayerBeadsIcon} />
        </NavLink>
        <NavLink exact activeClassName={classes.activeNavlink} to="/gourd-seeds" route="/gourd-seeds" onClick={this.updateRoute}>
          <BottomNavigationAction showLabel={true} label="Gourd Seeds" aria-label="Gourd Seeds" icon={gourdSeedsIcon} />
        </NavLink>
        <NavLink exact activeClassName={classes.activeNavlink} to="/memories" route="/memories" onClick={this.updateRoute}>
          <BottomNavigationAction showLabel={true} label="Memories" aria-label="Memories" icon={memoriesIcon} />
        </NavLink>
        <NavLink exact activeClassName={classes.activeNavlink} to="/prosthetics" route="/prosthetics" onClick={this.updateRoute}>
          <BottomNavigationAction showLabel={true} label="Prosthetics" aria-label="Prosthetics" icon={prostheticsIcon} />
        </NavLink>
        <NavLink exact activeClassName={classes.activeNavlink} to="/carp-scales" route="/carp-scales" onClick={this.updateRoute}>
          <BottomNavigationAction showLabel={true} label="Scales" aria-label="Carp Scales" icon={scaleIcon} />
        </NavLink>
      </BottomNavigation>
    );
  }
}

SimpleBottomNavigation.propTypes = {
  classes: PropTypes.object.isRequired,
};

export default withStyles(styles)(SimpleBottomNavigation);
