import React from 'react';
import PropTypes from 'prop-types';
import { withStyles } from '@material-ui/core/styles';
// Material Components
import Grid from '@material-ui/core/Grid';
// Components
import { useFilters } from "../Filters";
import ItemDetails from './ItemDetails';

const styles = theme => ({
  root: {
    flexGrow: 1,
  },
  griditem: {
    padding: theme.spacing(2),
  }
});

function DataItemCard(props) {

  const filtersState = useFilters();

  const { classes, data, dataGeneral } = props;

  return (
    <div>
      <Grid container justify="center" spacing={24}>
        {data.map(data => (
          <Grid item xs={12} sm={6} md={4} className={classes.griditem}>
            {console.log(filtersState.filters[0])}
            {filtersState.filters[0].active &&
              filtersState.filters.map(filter => (
              filter.id === data.filterId && filter.active ?
                <ItemDetails 
                  data={data}
                  dataGeneral={dataGeneral}
                /> : null
            ))}
          </Grid>
        ))}
      </Grid>
    </div>
  );
}

DataItemCard.propTypes = {
  classes: PropTypes.object.isRequired,
};

export default withStyles(styles)(DataItemCard);
