import React from "react";
import PropTypes from 'prop-types';
import { withStyles } from '@material-ui/core/styles';
// Constants
import { BASE_IMG_URL } from '../constants/constants';
// Material Components
import CardMedia from '@material-ui/core/CardMedia';

const styles = theme => ({
  card: {
    maxWidth: 345,
    minHeight: '100%',
    display: 'flex',
    flexWrap: 'wrap',
    '& > *': {
      flex: '1 1 100%',
    }
  },
  media: {
    // ⚠️ object-fit is not supported by IE 11.
    objectFit: 'cover',
    margin: `auto`,
  },
});

const _loaded = {};

class ItemImageLoader extends Component {
  
  //initial state: image loaded stage 
  state = {
    loaded: _loaded[this.props.src]
  };

  //define our loading and loaded image classes
  static defaultProps = {
    loadingClassName: "img-loading",
    loadedClassName: "img-loaded"
  };

  //image onLoad handler to update state to loaded
  onLoad = () => {
    _loaded[this.props.src] = true;
    this.setState(() => ({ loaded: true }));
  };

  render() {
  
    let { classNames, loadedClassName, loadingClassName, data, ...props } = this.props;

    classNames = `${classes.media} ${this.state.loaded
      ? loadedClassName
      : loadingClassName}`;

    return (
      <CardMedia
        component="img"
        className={classNames} 
        onLoad={this.onLoad}
        height="auto"
        image={data.img && ( BASE_IMG_URL+data.img )}
        title={data.title}
        alt-title={data.title}
        loading="lazy"
      />
    )
  }
}

ItemImageLoader.propTypes = {
  classes: PropTypes.object.isRequired,
};

export default withStyles(styles)(ItemImageLoader);
