import React from 'react';
import PropTypes from 'prop-types';
import { withStyles } from '@material-ui/core/styles';
// Material Components
import AppBar from '@material-ui/core/AppBar';
import Toolbar from '@material-ui/core/Toolbar';
import Typography from '@material-ui/core/Typography';
import Switch from '@material-ui/core/Switch';
import FormControlLabel from '@material-ui/core/FormControlLabel';
import FormGroup from '@material-ui/core/FormGroup';

const styles = {
  root: {
    flexGrow: 1,
  },
  grow: {
    flexGrow: 1,
  },
};

class MenuAppBar extends React.Component {
  state = {
    theme: true,
  };

  handleChange = event => {
    this.setState({ theme: event.target.checked });
  };

  render() {
    const { classes } = this.props;
    const { theme } = this.state;

    return (
      <div className={classes.root}>
        <AppBar position="static" color="inherit">
          <Toolbar>
            <Typography variant="title" color="inherit" align="left" className={classes.grow}>
              Get Your Prayer Beads
              <Typography inline={true} variant="subtitle2"><em>And other things...</em></Typography>
            </Typography>
            <FormGroup>
              <FormControlLabel
                control={
                  <Switch checked={theme} onChange={this.handleChange} aria-label="ThemeSwitch" />
                }
                label={theme ? 'Light Theme' : 'Dark Theme'}
              />
            </FormGroup>
          </Toolbar>
        </AppBar>
      </div>
    );
  }
}

MenuAppBar.propTypes = {
  classes: PropTypes.object.isRequired,
};

export default withStyles(styles)(MenuAppBar);
