import React from 'react';
// Components
import { ThemeProvider as MaterialThemeProvider } from '@material-ui/styles';
import CssBaseline from '@material-ui/core/CssBaseline';
import SetTheme from "./SetTheme.js";

const defaultContextData = {
  light: false,
  toggle: () => {}
};

const ThemeContext = React.createContext(defaultContextData);
const useTheme = () => React.useContext(ThemeContext);

const useEffectDarkMode = () => {
  const [themeState, setThemeState] = React.useState({
    light: false,
    hasThemeMounted: false
  });
  React.useEffect(() => {
    const lsDark = localStorage.getItem("light") === "true";
    setThemeState({ ...themeState, light: lsDark, hasThemeMounted: true });
  }, []);

  return [themeState, setThemeState];
};

const ThemeProvider = ({ children }) => {
  const [themeState, setThemeState] = useEffectDarkMode();

  if (!themeState.hasThemeMounted) {
    return <div />;
  }

  const toggle = () => {
    const light = !themeState.light;
    localStorage.setItem("light", JSON.stringify(light));
    setThemeState({ ...themeState, light });
  };

  const computedTheme = themeState.light ? SetTheme("light") : SetTheme("dark");

  return (
    <MaterialThemeProvider theme={computedTheme}>
      <ThemeContext.Provider
        value={{
          dark: themeState.dark,
          toggle
        }}
      >
        <CssBaseline />
        {children}
      </ThemeContext.Provider>
    </MaterialThemeProvider>
  );
};

export { ThemeProvider, useTheme };
