import React from 'react';
import PropTypes from 'prop-types';
import { withStyles } from '@material-ui/core/styles';
// Material Components
import List from '@material-ui/core/List';
import ListItem from '@material-ui/core/ListItem';
import ListItemText from '@material-ui/core/ListItemText';
import Typography from '@material-ui/core/Typography';
import Divider from '@material-ui/core/Divider';
import Avatar from '@material-ui/core/Avatar';
import Tooltip from '@material-ui/core/Tooltip';
import NewReleasesIcon from '@material-ui/icons/NewReleases';
import SearchIcon from '@material-ui/icons/Search';
import MonetizationOnIcon from '@material-ui/icons/MonetizationOn';
import WarningIcon from '@material-ui/icons/Warning';
import InfoIcon from '@material-ui/icons/Info';

const styles = theme => ({
  root: {
    width: '100%',
    backgroundColor: theme.palette.background.paper,
  },
  listText: {
    padding: `0 16px`,
  },
  typoIndent: {
    display: 'block',
    paddingLeft: '72px',
  },
  typoAnno: {
    display: 'block',
  },
  typoAnnoIcon: {
    verticalAlign: 'middle',
    marginRight: '5px',
  }
});

function FolderList(props) {
  const { classes, data } = props;
  const baseUrl = `/assets/images/`;

  return (
    <List 
      className={classes.root}
      title={data.title}
      location={data.location}
      description={data.description}
      procurement={data.procurement}
    >
      <Divider />
      <ListItem>
        <Tooltip title={data.procurement} placement="left">
          <Avatar>
            {data.procurement === 'Rewarded' && (<NewReleasesIcon />)}
            {data.procurement === 'Discovered' && (<SearchIcon />)}
            {data.procurement === 'Purchased' && (<MonetizationOnIcon />)}
            {data.img && ( <img src={baseUrl+data.img} alt="logo" width="50" /> )}
          </Avatar>
        </Tooltip>
        <ListItemText className={classes.listText} primary={data.title} secondary={data.location} />
      </ListItem>
      <Typography className={classes.typoIndent} align="left" variant="body2" gutterBottom={false}>
        {data.description}
        {data.requirement && (
            <Typography className={classes.typoAnno} align="left" variant="caption" gutterBottom={false} color="textSecondary">
              <WarningIcon className={classes.typoAnnoIcon} /> 
              {data.requirement}
            </Typography>
          )
        }
        {data.note && (
            <Typography className={classes.typoAnno} align="left" variant="caption" gutterBottom={false} color="textSecondary">
              <InfoIcon className={classes.typoAnnoIcon} /> 
              {data.note}
            </Typography>
          )
        }
      </Typography>
    </List>
  );
}

FolderList.propTypes = {
  classes: PropTypes.object.isRequired,
};

export default withStyles(styles)(FolderList);
