import React from 'react';
import PropTypes from 'prop-types';
import { withStyles } from '@material-ui/core/styles';
// Material Components
import ExpansionPanel from '@material-ui/core/ExpansionPanel';
import ExpansionPanelSummary from '@material-ui/core/ExpansionPanelSummary';
import ExpansionPanelDetails from '@material-ui/core/ExpansionPanelDetails';
import Typography from '@material-ui/core/Typography';
import ExpandMoreIcon from '@material-ui/icons/ExpandMore';
import Chip from '@material-ui/core/Chip';
// Components
import LocationDetails from './LocationDetails';

const styles = theme => ({
  root: {
    width: '100%',
    '&:not(:last-child)': {
      borderBottom: 0,
    },
  },
  expanded: {
    margin: 'auto',
  },
  heading: {
    fontSize: theme.typography.pxToRem(20),
    fontWeight: theme.typography.fontWeightRegular,
  },
  expanPanDet: {
    flexWrap: 'wrap',
  },
});

const StyledChip = withStyles({
  root:  {
    marginLeft: '10px',
    height: '22px',
  },
  label: {
    paddingLeft: '8px',
    paddingRight: '8px',
  }
})(Chip);

function DataExpansionPanel(props) {
  const { classes, data } = props;
  const baseUrl = `/assets/images/`;

  return (
    <div className={classes.root}>
      {data.map(region => (
        <ExpansionPanel
          key={region.id}
          count={region.count}
          location={region.location}
          location_img={region.location_img}
          locations={region.locations}
        >
          <ExpansionPanelSummary expandIcon={<ExpandMoreIcon />}>
            <Typography className={classes.heading}>
              {region.location} 
              <StyledChip label={region.count}></StyledChip>
            </Typography>
          </ExpansionPanelSummary>
          <ExpansionPanelDetails className={classes.expanPanDet}>
          {region.location_img && (<img src={baseUrl+region.location_img} alt={region.location} width='100%' />)}
            {region.locations.map(data => (
              <LocationDetails 
                data={data}
              />
            ))}
          </ExpansionPanelDetails>
        </ExpansionPanel>
      ))}
    </div>
  );
}

DataExpansionPanel.propTypes = {
  classes: PropTypes.object.isRequired,
};

export default withStyles(styles)(DataExpansionPanel);
