import React from 'react';
import PropTypes from 'prop-types';
import { withStyles } from '@material-ui/core/styles';
// Components
import DialogueBox from './DialogueBox';
// Material Components
import Divider from '@material-ui/core/Divider';
import Avatar from '@material-ui/core/Avatar';
import Tooltip from '@material-ui/core/Tooltip';
import Chip from '@material-ui/core/Chip';
import Typography from '@material-ui/core/Typography';
import AppBar from '@material-ui/core/AppBar';

import Card from '@material-ui/core/Card';
import CardContent from '@material-ui/core/CardContent';
import CardMedia from '@material-ui/core/CardMedia';
import CardActions from '@material-ui/core/CardActions';

import List from '@material-ui/core/List';
import ListItem from '@material-ui/core/ListItem';
import ListItemText from '@material-ui/core/ListItemText'

import Table from '@material-ui/core/Table';
import TableBody from '@material-ui/core/TableBody';
import TableCell from '@material-ui/core/TableCell';
import TableHead from '@material-ui/core/TableHead';
import TableRow from '@material-ui/core/TableRow';

import Tabs from '@material-ui/core/Tabs';
import Tab from '@material-ui/core/Tab';

import NewReleasesIcon from '@material-ui/icons/NewReleases';
import SearchIcon from '@material-ui/icons/Search';
import MonetizationOnIcon from '@material-ui/icons/MonetizationOn';
import WarningIcon from '@material-ui/icons/Warning';
import InfoIcon from '@material-ui/icons/Info';
import CheckCircleIcon from '@material-ui/icons/CheckCircle';
import OfflineBoltIcon from '@material-ui/icons/OfflineBolt';

const styles = theme => ({
  card: {
    maxWidth: 345,
    width: '100%',
    minHeight: 415,
  },
  media: {
    // ⚠️ object-fit is not supported by IE 11.
    objectFit: 'cover',
  },
  listText: {
    padding: `0 16px`,
    '& .MuiListItemText-primary': {
      fontWeight: `bold`,
      fontSize: `1.3em`,
    },
  },
  typoIndent: {
    display: 'block',
    paddingLeft: '72px',
  },
  listAnno: {
    '& li:last-of-type': {
      border: 0,
    }
  },
  typoAnno: {
    display: 'block',
  },
  typoAnnoIcon: {
    verticalAlign: 'middle',
    marginRight: '5px',
  },
  typoSummIconWrap: {
    float: 'right',
  },
  typoSummIcon: {
    verticalAlign: 'middle',
    marginLeft: '3px',
    marginTop: '3px',
  },
  divider: {
    margin: '20px auto',
  },
  avatarIcon: {
    margin: '0 10px 10px 0',
    float: 'left',
  },
  inlineTitle: {
    textTransform: 'uppercase',
    letterSpacing: '0.2em',
  }
});

const CounterChip = withStyles({
  root:  {
    height: '22px',
    marginRight: 5,
  },
  label: {
    paddingLeft: '8px',
    paddingRight: '8px',
  }
})(Chip);

function ItemCard(props) {
  const { classes, data, dataGeneral } = props;
  const baseUrl = `/assets/images/`;

  const dialogueContentGeneral = (data, dataGeneral, classes) => {
    return (
      <Typography variant="body1">
        {data.description}
        <List className={classes.listAnno}>
          {data.count && (
            <ListItem divider={true}>
              <Typography className={classes.typoAnno} align="left" variant="caption" gutterBottom={false} color="textSecondary">
                <CounterChip label={data.count}></CounterChip>
                {dataGeneral.countname}
              </Typography>
            </ListItem>
          )}
          {data.requirement && (
            <ListItem divider={true}>
              <Typography className={classes.typoAnno} align="left" variant="caption" gutterBottom={false} color="textSecondary">
                <WarningIcon className={classes.typoAnnoIcon} /> 
                {data.requirement}
              </Typography>
            </ListItem>
            )
          }
          {data.note && (
            <ListItem divider={true}>
              <Typography className={classes.typoAnno} align="left" variant="caption" gutterBottom={false} color="textSecondary">
                <InfoIcon className={classes.typoAnnoIcon} /> 
                {data.note}
              </Typography>
            </ListItem>
            )
          }
          {data.utility && (
            <ListItem divider={true}>
              <Typography className={classes.typoAnno} align="left" variant="caption" gutterBottom={false} color="textSecondary">
                <CheckCircleIcon className={classes.typoAnnoIcon} /> 
                {data.utility}
              </Typography>
            </ListItem>
            )
          }
          {data.effect && (
            <ListItem divider={true}>
              <Typography className={classes.typoAnno} align="left" variant="caption" gutterBottom={false} color="textSecondary">
                <OfflineBoltIcon className={classes.typoAnnoIcon} /> 
                {data.effect}
              </Typography>
            </ListItem>
            )
          }
        </List>
        {data.procurement && ( 
          <Typography variant="body2" gutterBottom={false}>
            <Divider className={classes.divider} />
            <Tooltip title={data.procurement} placement="left">
              <Avatar className={classes.avatarIcon}>
                {data.procurement === 'Rewarded' && (<NewReleasesIcon />)}
                {data.procurement === 'Discovered' && (<SearchIcon />)}
                {data.procurement === 'Purchased' && (<MonetizationOnIcon />)}
              </Avatar>
            </Tooltip>
          </Typography>
        )}
        {data.find && (
          <Typography variant="body2">
            <strong className={classes.inlineTitle}>Find them:</strong> {data.find}
          </Typography>
          )
        }
      </Typography>
    )
  }

  const dialogueContentList = (data, dataGeneral, classes) => {
    return (
      <div>
        <AppBar position="fixed">
          <Tabs value={tabValue} onChange={this.handleTabChange}>
            {data.obtain && <Tab value="find" label="Find" /> }
            {data.farm && <Tab value="farm" label="Farm" /> }
          </Tabs>
        </AppBar>
        {value === 'find' && (
          <Typography variant="body1">
            <List className={classes.listAnno}>
              {data.obtain.map(item => (
                <ListItem 
                  divider={true} 
                  dense={true} 
                  disableGutters={true} 
                  key={item.id}
                >
                  <Typography className={classes.typoAnno} align="left" variant="caption" gutterBottom={false} color="textSecondary">
                    <ListItemText className={classes.listText} primary={item.location} secondary={item.find} />
                  </Typography>
                </ListItem>
              ))}
            </List>
          </Typography>
        )}
        {value === 'farm' && (
          <Typography variant="body1">
            <List className={classes.listAnno}>
              {data.farm.map(item => (
                <ListItem 
                  divider={true} 
                  dense={true} 
                  disableGutters={true} 
                  key={item.id}
                >
                  <Typography className={classes.typoAnno} align="left" variant="caption" gutterBottom={false} color="textSecondary">
                    <ListItemText className={classes.listText} primary={item.enemy} secondary={item.location} />
                  </Typography>
                </ListItem>
              ))}
            </List>
          </Typography>
        )}
      </div>
    )
  }

  const dialogueContentTable = (data, dataGeneral, classes) => {
    return (
      <Typography variant="body1">
        <Table className={classes.table}>
          <TableHead>
            <TableRow>
              <TableCell>Upgrade</TableCell>
              <TableCell align="right">Qty</TableCell>
            </TableRow>
          </TableHead>
          <TableBody>
            {data.usage && (
              data.usage.map(row => (
                <TableRow key={row.id}>
                  <TableCell component="th" scope="row">
                    {row.upgrade}
                  </TableCell>
                  <TableCell align="right"><CounterChip label={row.qty}></CounterChip></TableCell>
                </TableRow>
              ))
            )}
          </TableBody>
        </Table>
      </Typography>
    )
  }

  return <ItemCardComponent />;
}

class ItemCardComponent extends React.Component {
  state = {
    tabValue: 'find',
  };

  handleTabChange = (event, value) => {
    this.setState({ value });
  };

  render() {
    return (
      <Card className={classes.card}>
        <CardMedia
          component="img"
          className={classes.media}
          height="auto"
          image={data.img && ( baseUrl+data.img )}
          title={data.title}
          alt-title={data.title}
        />
        <CardContent>
          <Typography variant="headline" component="h3">
            {data.title}
          </Typography>
          <Divider />
          {data.location && (
            <Typography gutterBottom variant="body2" component="h6">
              {data.location}
            </Typography>
          )}
          {(data.utility || data.effect) && (
            <Typography className={classes.typoAnno} align="right" gutterBottom={false} color="textSecondary">
              {data.utility && (
                  <Tooltip title={data.utility} placement="top">
                    <Typography className={classes.typoSummIconWrap}>
                      <CheckCircleIcon className={classes.typoSummIcon} /> 
                    </Typography>
                  </Tooltip>
                )
              }
              {data.effect && (
                  <Tooltip title={data.effect} placement="top">
                    <Typography className={classes.typoSummIconWrap}>
                      <OfflineBoltIcon className={classes.typoSummIcon} /> 
                    </Typography>
                  </Tooltip>
                )
              }
            </Typography>
          )}
        </CardContent>
        <CardActions>
          {dataGeneral.btnGeneral && (
            <DialogueBox data={data} dataGeneral={dataGeneral} type="general">
              <Typography gutterBottom>
                {dialogueContentGeneral(data, dataGeneral, classes)}
              </Typography>
            </DialogueBox>
          )}
          {dataGeneral.btnList && (
            <DialogueBox data={data} dataGeneral={dataGeneral} type="list">
              <Typography gutterBottom>
                {dialogueContentList(data, dataGeneral, classes)}
              </Typography>
            </DialogueBox>
          )}
          {dataGeneral.btnTable && (
            <DialogueBox data={data} dataGeneral={dataGeneral} type="table">
              <Typography gutterBottom>
                {dialogueContentTable(data, dataGeneral, classes)}
              </Typography>
            </DialogueBox>
          )}
        </CardActions>
      </Card>
    );
  }
}

ItemCard.propTypes = {
  classes: PropTypes.object.isRequired,
};

export default withStyles(styles)(ItemCard);
