import React from 'react';
import PropTypes from 'prop-types';
import { withStyles } from '@material-ui/core/styles';
// Material Components
import Typography from '@material-ui/core/Typography';
import Divider from '@material-ui/core/Divider';
import Avatar from '@material-ui/core/Avatar';
import Tooltip from '@material-ui/core/Tooltip';
import Chip from '@material-ui/core/Chip';

import ListItem from '@material-ui/core/ListItem';
import ListItemText from '@material-ui/core/ListItemText'

import NewReleasesIcon from '@material-ui/icons/NewReleases';
import SearchIcon from '@material-ui/icons/Search';
import MonetizationOnIcon from '@material-ui/icons/MonetizationOn';
import WarningIcon from '@material-ui/icons/Warning';
import InfoIcon from '@material-ui/icons/Info';

const styles = theme => ({
  root: {
    width: '100%',
  },
  listText: {
    padding: `0 16px`,
  },
  typoIndent: {
    display: 'block',
    paddingLeft: '72px',
  },
  typoAnno: {
    display: 'block',
  },
  typoAnnoIcon: {
    verticalAlign: 'middle',
    marginRight: '5px',
  }
});

const TextChip = withStyles({
  root:  {
    height: '22px',
    marginRight: 5,
  },
  label: {
    paddingLeft: '8px',
    paddingRight: '8px',
  }
})(Chip);

function DetailList(props) {
  const { classes, data } = props;

  return (
    <React.Fragment>
      {data.procurement && ( 
        <Typography gutterBottom variant="body2" component="h6" align="right">
          <TextChip label={data.procurement} className={classes.textChipBadge}></TextChip>
        </Typography>
      )}
      <ListItem disableGutters={true} dvider={true} >
        <ListItemText className={classes.listText} primary={data.title} secondary={data.location} />
      </ListItem>
    </React.Fragment>
  );

  /*return (
    <List 
      className={classes.root}
      title={data.title}
      location={data.location}
      description={data.description}
      procurement={data.procurement}
    >
      <Divider />
      <ListItem>
        {data.procurement && ( 
          <Tooltip title={data.procurement} placement="left">
            <Avatar>
              {data.procurement === 'Rewarded' && (<NewReleasesIcon />)}
              {data.procurement === 'Discovered' && (<SearchIcon />)}
              {data.procurement === 'Purchased' && (<MonetizationOnIcon />)}
            </Avatar>
          </Tooltip>
        )}
        {data.img && ( <img src={BASE_IMG_URL+data.img} alt="logo" width="50" /> )}
        <ListItemText className={classes.listText} primary={data.title} secondary={data.location} />
      </ListItem>
      <Typography className={classes.typoIndent} align="left" variant="body2" gutterBottom={false}>
        {data.description}
        {data.requirement && (
            <Typography className={classes.typoAnno} align="left" variant="caption" gutterBottom={false} color="textSecondary">
              <WarningIcon className={classes.typoAnnoIcon} /> 
              {data.requirement}
            </Typography>
          )
        }
        {data.note && (
            <Typography className={classes.typoAnno} align="left" variant="caption" gutterBottom={false} color="textSecondary">
              <InfoIcon className={classes.typoAnnoIcon} /> 
              {data.note}
            </Typography>
          )
        }
      </Typography>
    </List>
  );*/
}

DetailList.propTypes = {
  classes: PropTypes.object.isRequired,
};

export default withStyles(styles)(DetailList);
