import React from 'react';
import PropTypes from 'prop-types';
import { withStyles } from '@material-ui/core/styles';
// Components
import { useTheme } from "../theme/ThemeContext";
// Material Components
import FormGroup from '@material-ui/core/FormGroup';
import FormControlLabel from '@material-ui/core/FormControlLabel';

import Checkbox from '@material-ui/core/Checkbox';
import Paper from '@material-ui/core/Paper';

const styles = theme => ({
  formGroup: {
    flexDirection: 'row',
    justifyContent: 'space-between',
    [theme.breakpoints.down('sm')]: {  
      justifyContent: 'space-around',
    },
  },
  formWrap: {
    padding: '15px',
    margin: '10px 15px',
  },
});

const filters = (dataFilters) => {
  return (
    dataFilters.map(filter => (
      //this.setState({ filter: false })
      filter
    ))
  )
}

const Filters = (props) => {

  // state = filters(this.props.dataFilters);
  // state = {...this.props.dataFilters};

  //render() {

    const themeState = useTheme();
  
    //filters(this.props.dataFilters, this.state);

    const { classes, dataFilters } = props;
    //const { display } = this.state;

    return (
      <Paper className={classes.formWrap}>
        <FormGroup className={classes.formGroup}>
          {dataFilters.map(filter => (
            <FormControlLabel
              control={
                <Checkbox
                //checked={this.state.display}
                //onChange={event => this.setState({ display: event.target.checked })}
                //value={this.state.display}
                  value={filter}
                  color="primary"
                  onClick={() => themeState.filter(filter)}
                />
              }
              label={filter}
            />
          ))}
        </FormGroup>
      </Paper>
    )
  //}

}

Filters.propTypes = {
  classes: PropTypes.object.isRequired,
};

export default withStyles(styles)(Filters);
