import React from 'react';
import PropTypes from 'prop-types';
import { withStyles } from '@material-ui/core/styles';
// Constants
import { BASE_IMG_URL } from '../constants/constants';

// Material Components;
import Typography from '@material-ui/core/Typography';
import Divider from '@material-ui/core/Divider';
import Avatar from '@material-ui/core/Avatar';
import Tooltip from '@material-ui/core/Tooltip';
import Chip from '@material-ui/core/Chip';

import Card from '@material-ui/core/Card';
import CardContent from '@material-ui/core/CardContent';
import CardMedia from '@material-ui/core/CardMedia';
import CardActions from '@material-ui/core/CardActions';

import List from '@material-ui/core/List';
import ListItem from '@material-ui/core/ListItem';
import ListItemText from '@material-ui/core/ListItemText'

import NewReleasesIcon from '@material-ui/icons/NewReleases';
import SearchIcon from '@material-ui/icons/Search';
import MonetizationOnIcon from '@material-ui/icons/MonetizationOn';
import WarningIcon from '@material-ui/icons/Warning';
import InfoIcon from '@material-ui/icons/Info';

const styles = theme => ({
  root: {
    width: '100%',
    backgroundColor: theme.palette.background.paper,
  },
  card: {
    maxWidth: 345,
    minHeight: 415,
  },
  media: {
    // ⚠️ object-fit is not supported by IE 11.
    objectFit: 'cover',
    margin: `auto`,

  },
  listText: {
    padding: `0 16px`,
  },
  typoIndent: {
    display: 'block',
    paddingLeft: '72px',
  },
  typoAnno: {
    display: 'block',
  },
  typoAnnoIcon: {
    verticalAlign: 'middle',
    marginRight: '5px',
  }
});

const TextChip = withStyles({
  root:  {
    height: '22px',
    marginRight: 5,
  },
  label: {
    paddingLeft: '8px',
    paddingRight: '8px',
  }
})(Chip);

function DetailList(props) {
  const { classes, data } = props;

  return (
    <Card className={classes.card}>
      <Typography variant="headline" component="h3">
        {data.title}
      </Typography>
      <CardMedia
        component="img"
        className={classes.media}
        height="auto"
        image={data.img && ( BASE_IMG_URL+data.img )}
        title={data.title}
        alt-title={data.title}
      />
      <CardContent>
        <List className={classes.root}>
          <Divider />
            <ListItem>
              {data.procurement && ( 
                <Typography gutterBottom variant="body2" component="h6" align="right">
                  <TextChip label={data.procurement} className={classes.textChipBadge}></TextChip>
                </Typography>
              )}
              <ListItemText className={classes.listText} primary={data.title} secondary={data.location} />
            </ListItem>
        </List>
      </CardContent>
      <CardActions>
      </CardActions>
    </Card>
  );

