import React from 'react';
import PropTypes from 'prop-types';
import { withStyles } from '@material-ui/core/styles';
// Components
import { useFilters } from "../FiltersContext";
// Material Components
import FormGroup from '@material-ui/core/FormGroup';
import FormControlLabel from '@material-ui/core/FormControlLabel';

import Checkbox from '@material-ui/core/Checkbox';
import Paper from '@material-ui/core/Paper';

const styles = theme => ({
  formGroup: {
    flexDirection: 'row',
    justifyContent: 'space-between',
    [theme.breakpoints.down('sm')]: {  
      justifyContent: 'space-around',
    },
  },
  formWrap: {
    padding: '15px',
    margin: '10px 15px',
  },
});

const Filters = (props) => {
  const { classes } = props;

  const filtersState = useFilters();

    return (
      <Paper className={classes.formWrap}>
        <FormGroup className={classes.formGroup}>
          {filtersState.filters.map(filter => (
            <FormControlLabel
              control={
                <Checkbox
                  id={filter.id}
                  value={filter.val}
                  color="primary"
                  checked={filtersState.allActive ? true : filter.active}
                  onChange={(event) => filtersState.handleChangeCheckbox(filter.id, filter.val, event.target.checked)}
                />
              }
              label={filter.val}
            />
          ))}
        </FormGroup>
      </Paper>
    )
  //}

}

Filters.propTypes = {
  classes: PropTypes.object.isRequired,
};

export default withStyles(styles)({ Filters });
