import React from 'react';
import PropTypes from 'prop-types';
import { withStyles } from '@material-ui/core/styles';
// Components
import DialogueBox from './DialogueBox';
// Material Components
import Card from '@material-ui/core/Card';
import CardContent from '@material-ui/core/CardContent';
import CardMedia from '@material-ui/core/CardMedia';
import CardActions from '@material-ui/core/CardActions';
import Button from '@material-ui/core/Button';
import ExpansionPanel from '@material-ui/core/ExpansionPanel';
import ExpansionPanelSummary from '@material-ui/core/ExpansionPanelSummary';
import ExpansionPanelDetails from '@material-ui/core/ExpansionPanelDetails';
import ExpandMoreIcon from '@material-ui/icons/ExpandMore';
import Typography from '@material-ui/core/Typography';
import Divider from '@material-ui/core/Divider';
import WarningIcon from '@material-ui/icons/Warning';
import InfoIcon from '@material-ui/icons/Info';

const styles = theme => ({
  card: {
    maxWidth: 345,
  },
  media: {
    // ⚠️ object-fit is not supported by IE 11.
    objectFit: 'cover',
  },
});

const DialogueContent = (data, classes) => {
  return (
    <Typography variant="caption">
      {data.description}
      {data.requirement && (
          <Typography className={classes.typoAnno} align="left" variant="caption" gutterBottom={false} color="textSecondary">
            <WarningIcon className={classes.typoAnnoIcon} /> 
            {data.requirement}
          </Typography>
        )
      }
      {data.note && (
          <Typography className={classes.typoAnno} align="left" variant="caption" gutterBottom={false} color="textSecondary">
            <InfoIcon className={classes.typoAnnoIcon} /> 
            {data.note}
          </Typography>
        )
      }
    </Typography>
  )
}

function ItemCard(props) {
  const { classes, data } = props;
  const baseUrl = `/assets/images/`;

  return (
    <Card className={classes.card}>
      <CardMedia
        component="img"
        className={classes.media}
        height="auto"
        image={data.img && ( baseUrl+data.img )}
        title={data.title}
        alt-title={data.title}
      />
      <CardContent>
        <Typography variant="headline" component="h3">
          {data.title}
        </Typography>
        <Divider />
        <Typography gutterBottom variant="body2" component="h6">
          {data.location}
        </Typography>
        <ExpansionPanel>
          <ExpansionPanelSummary expandIcon={<ExpandMoreIcon />}>
            <Typography className={classes.heading}>Memory</Typography>
          </ExpansionPanelSummary>
          <ExpansionPanelDetails>
            <Typography variant="caption">
              {data.description}
              {data.requirement && (
                  <Typography className={classes.typoAnno} align="left" variant="caption" gutterBottom={false} color="textSecondary">
                    <WarningIcon className={classes.typoAnnoIcon} /> 
                    {data.requirement}
                  </Typography>
                )
              }
              {data.note && (
                  <Typography className={classes.typoAnno} align="left" variant="caption" gutterBottom={false} color="textSecondary">
                    <InfoIcon className={classes.typoAnnoIcon} /> 
                    {data.note}
                  </Typography>
                )
              }
            </Typography>
          </ExpansionPanelDetails>
        </ExpansionPanel>
      </CardContent>
      <CardActions>
        <Button size="small" color="inherit">
          Memory
        </Button>
        <DialogueBox>
          <Typography gutterBottom>
            <DialogueContent data={data} classes={classes} />
          </Typography>
        </DialogueBox>
      </CardActions>
    </Card>
  );
}

ItemCard.propTypes = {
  classes: PropTypes.object.isRequired,
};

export default withStyles(styles)(ItemCard);
