import React from 'react';
import PropTypes from 'prop-types';
import { withStyles } from '@material-ui/core/styles';
// Material Components
import List from '@material-ui/core/List';
import ListItem from '@material-ui/core/ListItem';
import ListItemText from '@material-ui/core/ListItemText';
import Typography from '@material-ui/core/Typography';
import Divider from '@material-ui/core/Divider';
import Avatar from '@material-ui/core/Avatar';
import Tooltip from '@material-ui/core/Tooltip';
import NewReleasesIcon from '@material-ui/icons/NewReleases';
import SearchIcon from '@material-ui/icons/Search';
import ReportIcon from '@material-ui/icons/Report';

const styles = theme => ({
  root: {
    width: '100%',
    backgroundColor: theme.palette.background.paper,
  },
  listText: {
    padding: `0 16px`,
  },
  typoIndent: {
    display: 'block',
    paddingLeft: '72px',
  },
  typoReq: {
    display: 'block',
  },
  typoReqIcon: {
    verticalAlign: 'middle',
  }
});

function FolderList(props) {
  const { classes, prayerbead } = props;
  return (
    <List 
      className={classes.root}
      title={prayerbead.title}
      location={prayerbead.location}
      description={prayerbead.description}
      procurement={prayerbead.procurement}
    >
      <Divider />
      <ListItem>
        <Tooltip title={prayerbead.procurement} placement="left">
          <Avatar>
            {prayerbead.isRewarded ?
              <NewReleasesIcon /> 
              :
              <SearchIcon /> 
            }
          </Avatar>
        </Tooltip>
        <ListItemText className={classes.listText} primary={prayerbead.title} secondary={prayerbead.location} />
      </ListItem>
      <Typography className={classes.typoIndent} align="left" variant="body2" gutterBottom={false}>
        {prayerbead.description}
        {prayerbead.requirement && (
            <Typography className={classes.typoReq} align="left" variant="caption" gutterBottom={false} color="textSecondary">
              <ReportIcon className={classes.typoReqIcon} /> 
              {prayerbead.requirement}
            </Typography>
          )
        }
      </Typography>
    </List>
  );
}

FolderList.propTypes = {
  classes: PropTypes.object.isRequired,
};

export default withStyles(styles)(FolderList);
