import React from 'react';
import PropTypes from 'prop-types';
import { withStyles } from '@material-ui/core/styles';
// Material Components
import BottomNavigation from '@material-ui/core/BottomNavigation';
import BottomNavigationAction from '@material-ui/core/BottomNavigationAction';
import RestoreIcon from '@material-ui/icons/Restore';
import FavoriteIcon from '@material-ui/icons/Favorite';
import LocationOnIcon from '@material-ui/icons/LocationOn';
import Switch from '@material-ui/core/Switch';
import FormControlLabel from '@material-ui/core/FormControlLabel';
import FormGroup from '@material-ui/core/FormGroup';

const styles = {
  root: {
    '&$selected': {
      color: 'red',
    },
    position: 'fixed',
    left: 0,
    right: 0,
    bottom: 0,
    borderTop: '1px solid #303030',
  },
  selected: {}
};

class SimpleBottomNavigation extends React.Component {
  state = {
    value: 0,
    theme: true,
  };

  changeTheme = event => {
    this.setState({ theme: event.target.checked });
  };

  handleChange = (event, value) => {
    this.setState({ value });
  };

  render() {
    const { classes } = this.props;
    const { theme } = this.state;

    return (
      <BottomNavigation
        value={value}
        onChange={this.handleChange}
        showLabels
        className={classes.root}
      >
        <FormGroup>
          <FormControlLabel
            control={
              <Switch color="inherit" checked={theme} onChange={this.changeTheme} aria-label="ThemeSwitch" />
            }
            label={theme ? 'Light Theme' : 'Dark Theme'}
          />
        </FormGroup>
        <BottomNavigationAction label="Recents" classes={{ root: classes.root, selected: classes.selected }} icon={<RestoreIcon />} />
        <BottomNavigationAction label="Favorites" icon={<FavoriteIcon />} />
        <BottomNavigationAction label="Nearby" icon={<LocationOnIcon />} />
      </BottomNavigation>
    );
  }
}

SimpleBottomNavigation.propTypes = {
  classes: PropTypes.object.isRequired,
};

export default withStyles(styles)(SimpleBottomNavigation);
