import React from 'react';
import PropTypes from 'prop-types';
import { withStyles } from '@material-ui/core/styles';
// Components
import { useTheme } from "./ThemeContext";
// Material Components
import IconButton from '@material-ui/core/IconButton';
import InvertColorsIcon from '@material-ui/icons/InvertColors';
import Tooltip from '@material-ui/core/Tooltip';

const styles = {
  root: {
    width: '100%',
  }
};

const ThemeSwitcher = () => {

  const themeState = useTheme();

  const checkTheme = () => {return localStorage.getItem("light")};

  return (
    <Tooltip title={checkTheme() ? 'Dark Theme' : 'Light Theme'} placement="left">
      <IconButton aria-label={localStorage.getItem("light") ? 'Dark Theme' : 'Light Theme'} onClick={() => themeState.toggle()}>
        <InvertColorsIcon fontSize="small" />
        {console.log(checkTheme())}
        {console.log(checkTheme() ? 'Dark Theme' : 'Light Theme')}
      </IconButton>
    </Tooltip>
  )

}

ThemeSwitcher.propTypes = {
  classes: PropTypes.object.isRequired,
};

export default withStyles(styles)(ThemeSwitcher);
