import React from 'react';
import PropTypes from 'prop-types';
import { withStyles } from '@material-ui/core/styles';
// Material Components
import FormGroup from '@material-ui/core/FormGroup';
import FormControlLabel from '@material-ui/core/FormControlLabel';

import Checkbox from '@material-ui/core/Checkbox';
import Paper from '@material-ui/core/Paper';

const styles = theme => ({
  root: {
    width: '100%',
  },
  formGroup: {
    flexDirection: 'row',
    justifyContent: 'space-between',
  },
  formWrap: {
    padding: '15px',
    margin: '10px 15px',
  },
});

const filters = (dataFilters) => {
  return (
    dataFilters.map(filter => (
      //this.setState({ filter: false })
      filter: false
    ))
  )
}

class Filters extends React.Component {

  state = filters(this.props.dataFilters);

  render() {
  
    //filters(this.props.dataFilters, this.state);

    const { classes, dataFilters } = this.props;
    const { display } = this.state;

    return (
      <Paper className={classes.formWrap}>
      {console.log('state:',this.state)}
        <FormGroup className={classes.formGroup}>
          {dataFilters.map(filter => (
            <FormControlLabel
              control={
                <Checkbox
                  checked={this.state.display}
                  onChange={event => this.setState({ display: event.target.checked })}
                  value={this.state.display}
                />
              }
              label={filter}
            />
          ))}
        </FormGroup>
      </Paper>
    )
  }

}

Filters.propTypes = {
  classes: PropTypes.object.isRequired,
};

export default withStyles(styles)(Filters);
