import React from 'react';
import PropTypes from 'prop-types';
import { withStyles } from '@material-ui/core/styles';
// Material Components
import Card from '@material-ui/core/Card';
import CardContent from '@material-ui/core/CardContent';
import CardMedia from '@material-ui/core/CardMedia';
import Typography from '@material-ui/core/Typography';
import Divider from '@material-ui/core/Divider';
import WarningIcon from '@material-ui/icons/Warning';
import InfoIcon from '@material-ui/icons/Info';

const styles = theme => ({
  card: {
    maxWidth: 345,
  },
  media: {
    // ⚠️ object-fit is not supported by IE 11.
    objectFit: 'cover',
  },
});

function ItemCard(props) {
  const { classes, data } = props;
  const baseUrl = `/assets/images/`;

  return (
    <Card className={classes.card}>
      <CardMedia
        component="img"
        className={classes.media}
        height="140"
        image={data.img && ( baseUrl+data.img )}
        title={data.title}
        alt-title={data.title}
      />
      <CardContent>
        <Typography variant="h5" component="h2">
          {data.title}
        </Typography>
        <Divider />
        <Typography gutterBottom variant="h6" component="h4">
          {data.location}
        </Typography>
        <Typography variant="body2">
          {data.description}
          {data.requirement && (
              <Typography className={classes.typoAnno} align="left" variant="caption" gutterBottom={false} color="textSecondary">
                <WarningIcon className={classes.typoAnnoIcon} /> 
                {data.requirement}
              </Typography>
            )
          }
          {data.note && (
              <Typography className={classes.typoAnno} align="left" variant="caption" gutterBottom={false} color="textSecondary">
                <InfoIcon className={classes.typoAnnoIcon} /> 
                {data.note}
              </Typography>
            )
          }
        </Typography>
      </CardContent>
    </Card>
  );
}

ItemCard.propTypes = {
  classes: PropTypes.object.isRequired,
};

export default withStyles(styles)(ItemCard);
