import React from 'react';
import PropTypes from 'prop-types';
import { withStyles } from '@material-ui/core/styles';
// Material Components
import AppBar from '@material-ui/core/AppBar';
import Toolbar from '@material-ui/core/Toolbar';
import Typography from '@material-ui/core/Typography';
import InvertColorsIcon from '@material-ui/icons/InvertColors';
import IconButton from '@material-ui/core/IconButton';
import Tooltip from '@material-ui/core/Tooltip';

const styles = {
  root: {
    flexGrow: 1,
  },
  grow: {
    flexGrow: 1,
  },
  AppBar: {
    padding: '5px 0',
  }
};

class MenuAppBar extends React.Component {
  state = {
    theme: true,
  };

  handleChange = (event, theme) => {
    let setTheme = true;
    event.target.theme ? setTheme = false : setTheme = true;
    console.log(event.target);
    this.setState({ theme: setTheme });
  };

  render() {
    const { classes } = this.props;
    const { theme } = this.state;
    const logo = '/assets/images/prayerbeads-white.png';

    return (
      <div className={classes.root}>
        <AppBar position="fixed" color="inherit" className={classes.AppBar}>
          <Toolbar>
            <Typography variant="h3" inline={true} color="inherit" align="left" className={classes.grow}>
              Get Your Prayer Beads <img src={logo} alt="logo" width="50" />
              <Typography variant="subtitle2"><em>And other things...</em></Typography>
            </Typography>
            <Tooltip theme={theme ? 'Light Theme' : 'Dark Theme'} placement="right">
            <div  onChange={this.handleChange} theme={theme}>
              <IconButton aria-label={theme ? 'Light Theme' : 'Dark Theme'}>
                <InvertColorsIcon fontSize="small" />
              </IconButton>
          </div>
            </Tooltip>
          </Toolbar>
        </AppBar>
      </div>
    );
  }
}

MenuAppBar.propTypes = {
  classes: PropTypes.object.isRequired,
};

export default withStyles(styles)(MenuAppBar);
