import React from 'react';
import PropTypes from 'prop-types';
import { withStyles } from '@material-ui/core/styles';
// Constants
import { BASE_IMG_URL } from '../constants/constants';
// Material Components
import Grid from '@material-ui/core/Grid';
import Typography from '@material-ui/core/Typography';
import Chip from '@material-ui/core/Chip';

import Card from '@material-ui/core/Card';
import CardHeader from '@material-ui/core/CardHeader';
import CardContent from '@material-ui/core/CardContent';
import CardMedia from '@material-ui/core/CardMedia';

import List from '@material-ui/core/List';

import ExpansionPanel from '@material-ui/core/ExpansionPanel';
import ExpansionPanelSummary from '@material-ui/core/ExpansionPanelSummary';
import ExpansionPanelDetails from '@material-ui/core/ExpansionPanelDetails';

import ExpandMoreIcon from '@material-ui/icons/ExpandMore';
// Components
import LocationDetails from './LocationDetails';

const styles = theme => ({
  root: {
    flexGrow: 1,
  },
  card: {
    minHeight: '100%',
  },
  cardTitle: {
    padding: '16px',
    flexDirection: 'row-reverse',
    justifyContent: 'flex-end',
    alignItems: 'flex-start',
    '& .MuiCardHeader-title': {
      fontSize: 18,
    },
    '& .MuiCardHeader-content': {
      flex: '0 0 auto',
    }
  },
  cardContent: {
    padding: 0,
  },
  media: {
    // ⚠️ object-fit is not supported by IE 11.
    objectFit: 'cover',
    margin: `auto`,

  },
  griditem: {
    padding: theme.spacing.unit * 2,
  },
  listAnno: {
    width: '100%',
    '& li:last-of-type': {
      border: 0,
    },
    '& .MuiButton-text': {
      margin: `0 10px`,
    }
  },
});

const CounterChip = withStyles({
  root:  {
    marginLeft: '10px',
    height: '22px',
  },
  label: {
    paddingLeft: '8px',
    paddingRight: '8px',
  }
})(Chip);

function CardGrid(props) {
  const { classes, data, dataGeneral } = props;
  const titleChip = <CounterChip label={data.map(data => data.count)}></CounterChip>;

  return (
    <div>
      <Grid container justify="center" spacing={24}>
        {data.map(region => (
          <Grid item xs={3} sm={6} className={classes.griditem}>
            <Card 
              key={region.id}
              count={region.count}
              location={region.location}
              location_img={region.location_img}
              locations={region.locations}
              className={classes.card}
            >
              <CardHeader
                avatar={
                  <CounterChip label={region.count}></CounterChip>
                }
                title={titleChip+region.location}
                subheader={region.location_note && ( region.location_note )}
                className={classes.cardTitle}
              />
              <CardMedia
                component="img"
                className={classes.media}
                height="auto"
                image={region.location_img && ( BASE_IMG_URL+region.location_img )}
                title={region.location}
                alt-title={region.location}
              />
              <CardContent className={classes.cardContent}>
                <List className={classes.listAnno} disablePadding={true}>
                  {region.locations.map(data => (
                      <LocationDetails 
                        data={data} dataGeneral={dataGeneral}
                      />
                  ))}
                </List>
              </CardContent>
            </Card>
          </Grid>
        ))}
      </Grid>
    </div>
  );

  /*return (
    <div>
      {data.map(region => (
        <ExpansionPanel
          key={region.id}
          count={region.count}
          location={region.location}
          location_img={region.location_img}
          locations={region.locations}
          className={classes.root}
        >
          <ExpansionPanelSummary expandIcon={<ExpandMoreIcon />}>
            <Typography className={classes.heading}>
              {region.location} 
              <CounterChip label={region.count}></CounterChip>
            </Typography>
          </ExpansionPanelSummary>
          <ExpansionPanelDetails className={classes.expanPanDet}>
            {region.location_img && (<img src={BASE_IMG_URL+region.location_img} alt={region.location} width='100%' />)}
            {region.locations.map(data => (
              <LocationDetails 
                data={data}
              />
            ))}
          </ExpansionPanelDetails>
        </ExpansionPanel>
      ))}
    </div>
  );*/
}

CardGrid.propTypes = {
  classes: PropTypes.object.isRequired,
};

export default withStyles(styles)(CardGrid);
