import React from 'react';
import PropTypes from 'prop-types';
import { withStyles } from '@material-ui/core/styles';
// Components
import DialogueBox from './DialogueBox';
// Material Components
import Card from '@material-ui/core/Card';
import CardContent from '@material-ui/core/CardContent';
import CardMedia from '@material-ui/core/CardMedia';
import CardActions from '@material-ui/core/CardActions';
import Button from '@material-ui/core/Button';
import Typography from '@material-ui/core/Typography';
import Divider from '@material-ui/core/Divider';
import WarningIcon from '@material-ui/icons/Warning';
import InfoIcon from '@material-ui/icons/Info';

const styles = theme => ({
  card: {
    maxWidth: 345,
    minHeight: 415,
  },
  media: {
    // ⚠️ object-fit is not supported by IE 11.
    objectFit: 'cover',
  },
});

function ItemCard(props) {
  const { classes, data } = props;
  const baseUrl = `/assets/images/`;

const dialogueContent = (data, classes) => {
  return (
    <Typography variant="body1">
      {data.description}
      {data.requirement && (
          <Typography className={classes.typoAnno} align="left" variant="caption" gutterBottom={false} color="textSecondary">
            <WarningIcon className={classes.typoAnnoIcon} /> 
            {data.requirement}
          </Typography>
        )
      }
      {data.note && (
          <Typography className={classes.typoAnno} align="left" variant="caption" gutterBottom={false} color="textSecondary">
            <InfoIcon className={classes.typoAnnoIcon} /> 
            {data.note}
          </Typography>
        )
      }
    </Typography>
  )
}

  return (
    <Card className={classes.card}>
      <CardMedia
        component="img"
        className={classes.media}
        height="auto"
        image={data.img && ( baseUrl+data.img )}
        title={data.title}
        alt-title={data.title}
      />
      <CardContent>
        <Typography variant="headline" component="h3">
          {data.title}
        </Typography>
        <Divider />
        <Typography gutterBottom variant="body2" component="h6">
          {data.location}
        </Typography>
      </CardContent>
      <CardActions>
        <DialogueBox data={data}>
          <Typography gutterBottom>
            {dialogueContent(data, classes)}
          </Typography>
        </DialogueBox>
      </CardActions>
    </Card>
  );
}

ItemCard.propTypes = {
  classes: PropTypes.object.isRequired,
};

export default withStyles(styles)(ItemCard);
