import React from 'react';
import PropTypes from 'prop-types';
import { withStyles } from '@material-ui/core/styles';
// Material Components
import Fab from '@material-ui/core/Fab';
import ExpandLessIcon from '@material-ui/icons/ExpandLess';

const styles = theme => ({
  scrollBtn: {
    margin: theme.spacing.unit,
    position: 'fixed',
    left: '2vh',
    bottom: '5vh',
    zIndex: '9999',
  },
});

const scrollToTop = () => {
  const rootContainer = document.getElementById('root');
  const rootContainerCo = rootContainer.scrollTop;
  if (rootContainerCo > 0) {
    window.requestAnimationFrame(scrollToTop);
    rootContainer.scrollTo(0, rootContainerCo - (rootContainerCo / 8));
  }
};

const scrollDisplay = () => {
  console.log('test');
};

class ToTop extends React.Component {

  render() {
    const { classes } = this.props;

    return (
      <Fab color="secondary" aria-label="Back to Top" size="small" className={classes.scrollBtn} onClick={scrollToTop} onScroll={scrollDisplay}>
        <ExpandLessIcon />
      </Fab>
    )
  }

}

ToTop.propTypes = {
  classes: PropTypes.object.isRequired,
};

export default withStyles(styles)(ToTop);
