import React, { useState } from 'react';
import PropTypes from 'prop-types';
import { withStyles } from '@material-ui/core/styles';
// Material Components
import FormGroup from '@material-ui/core/FormGroup';
import FormControlLabel from '@material-ui/core/FormControlLabel';

import Checkbox from '@material-ui/core/Checkbox';
import Paper from '@material-ui/core/Paper';

const styles = theme => ({
  formGroup: {
    flexDirection: 'row',
    justifyContent: 'space-between',
    [theme.breakpoints.down('sm')]: {  
      justifyContent: 'space-around',
    },
  },
  formWrap: {
    padding: '15px',
    margin: '10px 15px',
  },
});

const Filters = (props) => {
  
  //filters(this.props.dataFilters, this.state);

  const { classes, dataFilters } = props;
  //const { display } = this.state;

  // state = filters(this.props.dataFilters);
  // state = {...this.props.dataFilters};

  //render() {

    localStorage.setItem("Filter", 'All');

    const initialFilters = () => {
      return (''
      )
    }

    const [filters, setFilters] = useState(initialFilters);
    const [task, setActiveFilters] = useState('');

    const filter = (val) => {
      localStorage.setItem("Filter", val);
        const bool = localStorage.getItem("Filter") === "true";
        const current = localStorage.getItem("Filter");
        console.log(val);
        console.log(bool, current);
    }

    const handleChangeCheckbox = (id, val, event) => {
      setFilters(
        filters.map(filter => {
          if (filter.id === id) {
            return { ...filter, active: !filter.active };
          } else {
            return filter;
          }
        })
      );
      if (val) {
        setFilters(filters.concat({ id: id, val, active: false }));
      }
      setActiveFilters(event.target.value);
    };

    return (
      <Paper className={classes.formWrap}>
        <FormGroup className={classes.formGroup}>
          {dataFilters.map(filter => (
            <FormControlLabel
              control={
                <Checkbox
                //checked={this.state.display}
                //onChange={event => this.setState({ display: event.target.checked })}
                //value={this.state.display}
                  id={filter.id}
                  value={filter.val}
                  color="primary"
                  checked={filter.active}
                  onChange={() => handleChangeCheckbox(filter.id)}
                />
              }
              label={filter}
            />
          ))}
        </FormGroup>
      </Paper>
    )
  //}

}

Filters.propTypes = {
  classes: PropTypes.object.isRequired,
};

export default withStyles(styles)(Filters);
