import React from 'react';
import PropTypes from 'prop-types';
import { withStyles } from '@material-ui/core/styles';
// Components
import { useTheme, lsLight } from "./ThemeContext";
// Material Components
import IconButton from '@material-ui/core/IconButton';
import InvertColorsIcon from '@material-ui/icons/InvertColors';
import Tooltip from '@material-ui/core/Tooltip';

const styles = {
  root: {
    width: '100%',
  }
};

const ThemeSwitcher = () => {

  const themeState = useTheme();

  return (
    <Tooltip title={themeState.light ? 'Dark Theme' : 'Light Theme'} placement="right">
      <IconButton aria-label={themeState.light ? 'Dark Theme' : 'Light Theme'} onClick={() => themeState.toggle()}>
        <InvertColorsIcon fontSize="small" />
        {themeState.light ? console.log('Dark Theme') : console.log('Light Theme')}
        {console.log(themeState.setThemeState)}
        {console.log(lsLight)}
        {console.log(localStorage.getItem("light"))}
      </IconButton>
    </Tooltip>
  )

}

ThemeSwitcher.propTypes = {
  classes: PropTypes.object.isRequired,
};

export default withStyles(styles)(ThemeSwitcher);
