import React from 'react';
import PropTypes from 'prop-types';
import { withStyles } from '@material-ui/core/styles';
import {
  Route,
  NavLink,
  BrowserRouter as Router,
  Switch,
} from 'react-router-dom';
// Components
import PrayerBeads from '../containers/PrayerBeads';
import Memories from '../containers/Memories';
import Prosthetics from '../containers/Prosthetics';
import NotFound from '../containers/NotFound'
// Material Components
import BottomNavigation from '@material-ui/core/BottomNavigation';
import BottomNavigationAction from '@material-ui/core/BottomNavigationAction';
import Typography from '@material-ui/core/Typography';
import RestoreIcon from '@material-ui/icons/Restore';
import FavoriteIcon from '@material-ui/icons/Favorite';
import LocationOnIcon from '@material-ui/icons/LocationOn';

const styles = {
  root: {
    position: 'fixed',
    left: 0,
    right: 0,
    bottom: 0,
    borderTop: '1px solid #303030',
  }
};

class SimpleBottomNavigation extends React.Component {
  state = {
    value: 0,
  };

  handleChange = (event, value) => {
    this.setState({ value });
  };

  render() {
    const { classes } = this.props;
    const { value } = this.state;

    return (
      <BottomNavigation
        value={value}
        onChange={this.handleChange}
        showLabels
        className={classes.root}
      >
        <Router>
          <NavLink exact activeClassName="active" to="/prayer-beads">
            <BottomNavigationAction label="Prayer Beads" aria-label="Prayer Beads" icon={<RestoreIcon />} />
            <Typography variant="caption">Prayer Beads</Typography>
          </NavLink>
          <NavLink exact activeClassName="active" to="/memories">
            <BottomNavigationAction label="Memories" aria-label="Memories" icon={<FavoriteIcon />} />
            <Typography variant="caption">Memories</Typography>
          </NavLink>
          <NavLink exact activeClassName="active" to="/prosthetics">
            <BottomNavigationAction label="Prosthetics" aria-label="Prosthetics" icon={<LocationOnIcon />} />
            <Typography variant="caption">Prosthetics</Typography>
          </NavLink>
          <Switch>
            <Route exact path="/" component={PrayerBeads} />
            <Route path="/memories" component={Memories} />
            <Route path="/prosthetics" component={Prosthetics} />
            <Route component={NotFound} />
          </Switch>
        </Router>
      </BottomNavigation>
    );
  }
}

SimpleBottomNavigation.propTypes = {
  classes: PropTypes.object.isRequired,
};

export default withStyles(styles)(SimpleBottomNavigation);
