import React from 'react';
import PropTypes from 'prop-types';
import { withStyles } from '@material-ui/core/styles';
import { NavLink } from 'react-router-dom';
// Material Components
import BottomNavigation from '@material-ui/core/BottomNavigation';
import BottomNavigationAction from '@material-ui/core/BottomNavigationAction';
import Typography from '@material-ui/core/Typography';
import RestoreIcon from '@material-ui/icons/Restore';
import FavoriteIcon from '@material-ui/icons/Favorite';
import LocationOnIcon from '@material-ui/icons/LocationOn';

const styles = {
  root: {
    position: 'fixed',
    left: 0,
    right: 0,
    bottom: 0,
    borderTop: '1px solid #303030',
  }
};

class SimpleBottomNavigation extends React.Component {
  state = {
    route: '/',
  };

  updateRoute = (event, route) => {
    let newRoute = event.currentTarget.attributes.route.nodeValue;
    this.setState({ route: newRoute });
    console.log( newRoute );
    console.log('state route:',this.state);
  };

  render() {
    const { classes } = this.props;
    const { route } = this.state;

    return (
      <BottomNavigation
        route={route}
        showLabels
        className={classes.root}
      >
      {console.log('route:',route)}
        { route == '/' && (
          <NavLink exact activeClassName="active" to="/prayer-beads" route="/prayer-beads" onClick={this.updateRoute}>
            <BottomNavigationAction label="Prayer Beads" aria-label="Prayer Beads" icon={<RestoreIcon />} />
            <Typography variant="caption">Prayer Beads</Typography>
          </NavLink>
        )}
        <NavLink exact activeClassName="active" to="/memories" route="/memories" onClick={this.updateRoute}>
          <BottomNavigationAction label="Memories" aria-label="Memories" icon={<FavoriteIcon />} />
          <Typography variant="caption">Memories</Typography>
        </NavLink>
        <NavLink exact activeClassName="active" to="/prosthetics" route="/prosthetics" onClick={this.updateRoute}>
          <BottomNavigationAction label="Prosthetics" aria-label="Prosthetics" icon={<LocationOnIcon />} />
          <Typography variant="caption">Prosthetics</Typography>
        </NavLink>
      </BottomNavigation>
    );
  }
}

SimpleBottomNavigation.propTypes = {
  classes: PropTypes.object.isRequired,
};

export default withStyles(styles)(SimpleBottomNavigation);
