import React from 'react';
import PropTypes from 'prop-types';
import { withStyles } from '@material-ui/core/styles';
// Components
import HeaderNav from './HeaderNav';
// Material Components
import AppBar from '@material-ui/core/AppBar';
import Toolbar from '@material-ui/core/Toolbar';
import Drawer from '@material-ui/core/Drawer';
import Typography from '@material-ui/core/Typography';
import InvertColorsIcon from '@material-ui/icons/InvertColors';
import IconButton from '@material-ui/core/IconButton';
import MenuIcon from '@material-ui/icons/Menu';
import Tooltip from '@material-ui/core/Tooltip';

const drawerWidth = 240;

const styles = theme => ({
  root: {
    display: 'flex',
    flexGrow: 1,
  },
  grow: {
    flexGrow: 1,
  },
  AppBar: {
    [theme.breakpoints.up('sm')]: {
      width: `calc(100% - ${drawerWidth}px)`,
    },
    padding: '5px 0',
    '& .header-icon': {
      verticalAlign: 'middle',
    }
  },
  drawer: {
    [theme.breakpoints.up('sm')]: {
      width: drawerWidth,
      flexShrink: 0,
    },
  },
  appBar: {
    marginLeft: drawerWidth,
    [theme.breakpoints.up('sm')]: {
      width: `calc(100% - ${drawerWidth}px)`,
    },
  },
  menuButton: {
    marginRight: 20,
    [theme.breakpoints.up('sm')]: {
      display: 'none',
    },
  },
  toolbar: theme.mixins.toolbar,
  drawerPaper: {
    width: drawerWidth,
  },
  content: {
    flexGrow: 1,
    padding: theme.spacing.unit * 3,
  },
});

class MenuAppBar extends React.Component {
  state = {
    mobileOpen: false,
    isdark: this.props.isdark,
  };

  handleDrawerToggle = () => {
    this.setState(state => ({ mobileOpen: !state.mobileOpen }));
  };

  toggleDark = (event, isdark) => {
    let toggleDark = this.state.isdark ? false : true;
    this.setState({ isdark: toggleDark });
  };

  render() {
    const { classes, theme } = this.props;
    const isdark = this.state.isdark;
    const logo = '/assets/images/prayer-bead.png';

    const drawer = (
      <div>
        <div className={classes.toolbar} />
        <HeaderNav />
      </div>
    );

    return (
      <div className={classes.root}>
        <AppBar position="fixed" color="inherit" className={classes.AppBar}>
          <Toolbar>
            <Typography variant="h3" inline={true} color="inherit" align="left" className={classes.grow}>
              Get Your Prayer Beads <img src={logo} alt="logo" width="50" className="header-icon" />
              <Typography variant="subtitle2"><em>And other things...</em></Typography>
            </Typography>
            <Tooltip title={isdark ? 'Light Theme' : 'Dark Theme'} placement="right">
              <IconButton aria-label={isdark ? 'Light Theme' : 'Dark Theme'} onClick={this.toggleDark}>
                <InvertColorsIcon fontSize="small" />
              </IconButton>
            </Tooltip>
            <IconButton 
              className={classes.menuButton} 
              color="inherit" 
              aria-label="Open drawer"
              onClick={this.handleDrawerToggle}
            >
              <MenuIcon />
            </IconButton>
          </Toolbar>
        </AppBar>
        <nav className={classes.drawer}>
          <Drawer
            container={this.props.container}
            variant="temporary"
            anchor={theme.direction === 'rtl' ? 'right' : 'left'}
            open={this.state.mobileOpen}
            onClose={this.handleDrawerToggle}
            classes={{
              paper: classes.drawerPaper,
            }}
          >
            {drawer}
          </Drawer>
        </nav>
      </div>
    );
  }
}

MenuAppBar.propTypes = {
  classes: PropTypes.object.isRequired,
};

export default withStyles(styles, { withTheme: true })(MenuAppBar);
