import React from 'react';
import PropTypes from 'prop-types';
import { withStyles } from '@material-ui/core/styles';
// Material Components
import FormGroup from '@material-ui/core/FormGroup';
import FormControlLabel from '@material-ui/core/FormControlLabel';

import Checkbox from '@material-ui/core/Checkbox';
import Paper from '@material-ui/core/Paper';

const styles = theme => ({
  formGroup: {
    flexDirection: 'row',
    justifyContent: 'space-between',
    [theme.breakpoints.down('sm')]: {  
      justifyContent: 'space-around',
    },
  },
  formWrap: {
    padding: '15px',
    margin: '10px 15px',
  },
});

const defaultContextData = {
  filters: [
    { id: 1, val: `All`, active: true, }
  ]
};

const FilterContext = React.createContext(defaultContextData);
const useFilters = () => React.useContext(FilterContext);

const useEffectFilters = () => {
  const [filtersState, setFiltersState] = React.useState({
    filters: FilterContext
  });
  React.useEffect(() => {
    setFiltersState({ ...filtersState });
  }, []);

  return [filtersState, setFiltersState];
};

const FiltersProvider = (props) => {
  
  //filters(this.props.dataFilters, this.state);

  const { classes, dataFilters, children } = props;
  //const { display } = this.state;

  // state = filters(this.props.dataFilters);
  // state = {...this.props.dataFilters};

  //render() {

    //localStorage.setItem("Filter", 'All');

    const initialFilters = () => {
      return (
        dataFilters.map(filter => (
          //this.setState({ filter: false })
          filter
        ))
      )
    }

    const [filters, setFilters] = React.useState(initialFilters);
    const [allActive, setAllFilters] = React.useState(true);

    const [filtersState, setFiltersState] = useEffectFilters();
    
    //const [task, setActiveFilters] = useState('');

    /*const filter = (val) => {
      localStorage.setItem("Filter", val);
        const bool = localStorage.getItem("Filter") === "true";
        const current = localStorage.getItem("Filter");
        console.log(val);
        console.log(bool, current);
    }*/

    const handleChangeCheckbox = (id, val, event) => {
      //resetFilters(event ? true : false);
      setAllFilters (() => val === 'All' ? true : false);
      setFilters(
        filters.map(filter => {
          if (val === 'All' && event) {
            return { ...filter, active: !filter.active };
          } else if (filter.id === id) {
            return { ...filter, active: !filter.active };
          } else {
            return filter;
          }
        })
      )

/*       if(val === 'All') { 
        resetFilters(event ? true : false);
        setFilters(
          filters.map(filter => {
            if (allActive && filter.val === 'All') {
              return { ...filter, active: true };
            } else if (filter.id === id) {
              return { ...filter, active: !filter.active };
            }
          })
        )
      } else {
        resetFilters(false);
        setFilters(
          filters.map(filter => {
            if (filter.id === id) {
              return { ...filter, active: !filter.active };
            } else {
              return filter;
            }
          })
        )
      } */
      
      /*localStorage.getItem(id) ? 
        localStorage.removeItem(id)
        :
        localStorage.setItem(id, val);*/

      //setActiveFilters(event);
      console.log('event',event);

      //setFiltersState(filters.concat({ ...filtersState, id: id, val, active: false }));
    };

    return (
      <FilterContext.Provider
        value={{
          allActive: allActive,
          filters: filters,
        }}
      >
        <Paper /* className={classes.formWrap} */>
          <FormGroup /* className={classes.formGroup} */>
            {filters.map(filter => (
              <FormControlLabel
                control={
                  <Checkbox
                  //checked={this.state.display}
                  //onChange={event => this.setState({ display: event.target.checked })}
                  //value={this.state.display}
                    id={filter.id}
                    value={filter.val}
                    color="primary"
                    //checked={localStorage.getItem(filter.id) ? true : false}
                    checked={filter.active ? true : false}
                    onChange={(event) => handleChangeCheckbox(filter.id, filter.val, event.target.checked)}
                    //onChange={event => console.log('test:',event.target.checked)}
                    test={localStorage.getItem(filter.id) ? true : false}
                  />
                }
                label={filter.val}
              />
            ))}
          </FormGroup>
        </Paper>
          {children}
      </FilterContext.Provider>
    )
  //}

}

FiltersProvider.propTypes = {
  classes: PropTypes.object.isRequired,
};

export default withStyles(styles)({ FiltersProvider });
export { FiltersProvider, useFilters };
